import React from 'react';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';
import 'font-awesome/css/font-awesome.min.css';

function Hero() {
  const particlesInit = async (main) => {
    await loadFull(main);
  };

  const particlesLoaded = (container) => {
    console.log(container);
  };

  return (
    <section id="hero" className="hero">
      <Particles
        id="tsparticles"
        init={particlesInit}
        loaded={particlesLoaded}
        options={{
          background: {
            color: {
              value: "#000", // Set a dark background color to contrast with particles
            },
          },
          particles: {
            number: {
              value: 100, // Set the number of particles
              density: {
                enable: true,
                value_area: 800, // Control particle density
              },
            },
            color: {
              value: "#ffffff", // White particles
            },
            shape: {
              type: "circle", // Particle shape (circle)
            },
            opacity: {
              value: 0.3,
              random: true,
              anim: {
                enable: true,
                speed: 1,
                opacity_min: 0.1,
              },
            },
            size: {
              value: 3,
              random: true,
              anim: {
                enable: true,
                speed: 4,
                size_min: 0.1,
              },
            },
            links: {
              enable: true,
              distance: 150, // Connect particles with a line when they are near
              color: "#ffffff", // Line color
              opacity: 0.5,
              width: 1,
            },
            move: {
              enable: true,
              speed: 3, // Particle speed
              direction: "none",
              random: false,
              straight: false,
              outModes: {
                default: "out", // Particles should exit the screen
              },
            },
          },
          interactivity: {
            events: {
              onhover: {
                enable: true,
                mode: "repulse", // Repulse effect when hovering
              },
              onclick: {
                enable: true,
                mode: "push", // Add more particles on click
              },
            },
            modes: {
              repulse: {
                distance: 100,
                duration: 0.4,
              },
            },
          },
        }}
      />
      <div className="hero-content">
        <h1>Pankti Parikh</h1>
        <p className="hero-subtitle">Retail Manager | Computer Science Engineer</p>
        <div className="hero-links">
          <a href="mailto: parikhpankti023@gmail.com" className="hero-link">
            <i className="fas fa-envelope"></i> Email
          </a>
          <a href="https://www.linkedin.com/in/panktiparikh023" className="hero-link">
            <i className="fab fa-linkedin"></i> LinkedIn
          </a>
        </div>
      </div>
    </section>
  );
}

export default Hero;
