import React from 'react';
import Particles from 'react-tsparticles';

function Hero() {
  const particlesOptions = {
    particles: {
      number: {
        value: 50, // Number of particles
        density: {
          enable: true,
          value_area: 800, // Density control
        },
      },
      shape: {
        type: "circle", // Particle shape
      },
      move: {
        enable: true,
        speed: 1, // Speed of movement
      },
      size: {
        value: 3, // Size of the particles
      },
      links: {
        enable: true, // Enabling lines to connect particles
        distance: 150,
        color: "#ffffff",
        opacity: 0.4,
        width: 1,
      },
    },
    interactivity: {
      events: {
        onhover: {
          enable: true,
          mode: "repulse", // Repulse effect on hover
        },
      },
    },
  };

  return (
    <section id="hero" className="hero">
      <Particles options={particlesOptions} />
      <div className="hero-content">
        <h1>Pankti Parikh</h1>
        <p className="hero-subtitle">Retail Manager | Computer Science Engineer</p>
        <div className="hero-links">
          <a href="mailto:parikhpankti023@gmail.com" className="hero-link">
            <i className="fas fa-envelope"></i> Email
          </a>
          <a href="https://www.linkedin.com/in/panktiparikh023" className="hero-link">
            <i className="fab fa-linkedin"></i> LinkedIn
          </a>
        </div>
      </div>
    </section>
  );
}

export default Hero;
